// page init
jQuery(function(){
	initAnchors();
	initCarousel();
	initCycleCarousel();
	initSlideShow();
	initChildClasses();
	initDropDownClasses();
	initMobileMenu();
	initMobileNav();
	initSameHeight();
	initCustomHover();
	jQuery('input, textarea').placeholder();
});

// add classes if item has dropdown
function initDropDownClasses() {
	jQuery('#nav li').each(function() {
		var item = jQuery(this);
		var drop = item.find('ul, .sub-navigation-thumb');
		var link = item.find('a').eq(0);
		if(drop.length) {
			item.addClass('has-drop-down');
			if(link.length) link.addClass('has-drop-down-a');
		}
	});
}

// add classes on hover/touch
function initCustomHover() {
	lib.each(lib.queryElementsBySelector('.language-holder'), function(index, item) {
		new TouchHover({
			element: item
		});
	});
}

// add classes to support css3 selectors in old browsers
function initChildClasses() {
	jQuery('#nav').children(':last-child').addClass('last-child');
}

// mobile aside menu
function initMobileMenu() {
	var activeClass = 'selected';
	var activeOverlay = 'mouseover-overlay';
	var header = jQuery('#header');

	jQuery('#nav').each(function() {
		var holder = jQuery(this);
		var items = holder.find('li.has-drop-down');

		items.each(function() {
			var item = jQuery(this);
			var subMenu = item.find('>ul, >.sub-navigation-thumb');
			var link = item.find('>a>.drop-opener');

			if (subMenu.length) {
				var btnBack = subMenu.find('>.btn-back a');

				link.on('click', function(e) {
					e.preventDefault();
					item.addClass(activeClass);
				});
				btnBack.on('click', function(e) {
					e.preventDefault();
					item.removeClass(activeClass);
				});
			}

			items.on('mouseenter', function() {
				header.addClass(activeOverlay);
			}).on('mouseleave', function() {
				header.removeClass(activeOverlay);
			});
		});
	});
}

// scroll galleries init
function initCarousel() {
	jQuery('.section-holder .carousel').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		stretchSlideToMask: true,
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500
	});

	jQuery('.switchers-gallery').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.small-slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		activeClass: 'active',
		pagerLinks: '.pagination li',
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500,
		step: 1,
		onBeforeChange: function(that) {
			that.slides.eq(that.currentStep).find('a').trigger('click');
		}
	});
}

// cycle scroll gallery init
function initCycleCarousel() {
	jQuery('.banner .carousel').scrollAbsoluteGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		generatePagination: '.pagination',
		stretchSlideToMask: true,
		pauseOnHover: true,
		maskAutoSize: true,
		autoRotation: true,
		switchTime: 5000,
		animSpeed: 500
	});
}

// fade gallery init
function initSlideShow() {
	jQuery('.slideshow').fadeGallery({
		slides: '.big-slide',
		btnPrev: 'a.butoon-prev',
		btnNext: 'a.button-next',
		pagerLinks: '.switchers-gallery .small-slide',
		event: 'click',
		autoRotation: false,
		autoHeight: true,
		switchTime: 3000,
		animSpeed: 500
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('body').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'active',
		menuOpener: '.contact-opener',
		menuDrop: '.contact-slide'
	});

	jQuery('body').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'active-event',
		menuOpener: '.event-opener',
		menuDrop: '.next-event-slide'
	});

	jQuery('body').mobileNav({
		menuActiveClass: 'nav-active',
		menuOpener: '.navbar-toggle'
	});
}

// initialize smooth anchor links
function initAnchors() {
	new SmoothScroll({
		anchorLinks: '.anchor-link'
	});
}

// align blocks height
function initSameHeight() {
	jQuery('.section-holder').sameHeight({
		elements: 'p',
		flexible: true,
		multiLine: true,
		biggestHeight: true
	});

	jQuery('.event-section').sameHeight({
		elements: 'h3',
		flexible: true,
		multiLine: true,
		biggestHeight: true
	});

	jQuery('.event-section').sameHeight({
		elements: 'p',
		flexible: true,
		multiLine: true,
		biggestHeight: true
	});

	jQuery('ul.navbar-nav').sameHeight({
		elements: '.dropdown-menu, .sub-dropdown-menu, .sub-navigation-thumb',
		flexible: true,
		multiLine: true,
		biggestHeight: true
	});

	jQuery('.section-holder').sameHeight({
		elements: 'h2',
		flexible: true,
		multiLine: true,
		biggestHeight: true
	});

	jQuery('.section-holder').sameHeight({
		elements: 'h3',
		flexible: true,
		multiLine: true,
		biggestHeight: true
	});
}

